import {useState, useEffect} from 'react'

function App(props) {
  const[input, setInput] = useState(1)
  const[message, setMessage] = useState("整数を入力:")
  const doInput = (event) =&gt; {
    setInput(event.target.value)
  }

  //useEffectで素数をチェック
  useEffect(()=&gt;{
    var prime = true
    if (input === 1) {
      prime = false
    } else {
      for (var i =2;i &lt;= input/2;i++) {
        if(input % i === 0){
          prime = false
          break
        }
      }
    }
    setMessage(prime ? "素数です。":"素数ではない。")
  },[input])

